import React, { useState, useEffect } from 'react';

function App() {
  // State for tasks and selected date
  const [tasks, setTasks] = useState([]);
  const [selectedDate, setSelectedDate] = useState(new Date().toISOString().slice(0, 10));
  const [newTask, setNewTask] = useState({ title: '', description: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [editTaskId, setEditTaskId] = useState(null);
  const [editTask, setEditTask] = useState({ title: '', description: '' });

  // Fetch tasks from backend (replace URL with your backend if needed)
  useEffect(() => {
    setLoading(true);
    fetch('http://localhost:5000/api/tasks')
      .then(res => res.json())
      .then(data => {
        setTasks(data);
        setLoading(false);
      })
      .catch(() => {
        setTasks([]);
        setError('Failed to load tasks.');
        setLoading(false);
      });
  }, []);

  // Add new task
  const handleAddTask = (e) => {
    e.preventDefault();
    if (!newTask.title) return;
    setLoading(true);
    fetch('http://localhost:5000/api/tasks', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        ...newTask,
        date: selectedDate
      })
    })
      .then(res => res.json())
      .then(task => {
        setTasks([...tasks, task]);
        setNewTask({ title: '', description: '' });
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to add task.');
        setLoading(false);
      });
  };

  // Mark task as done
  const handleDone = (id) => {
    setLoading(true);
    fetch(`http://localhost:5000/api/tasks/${id}/done`, { method: 'PATCH' })
      .then(res => res.json())
      .then(updated => {
        setTasks(tasks.map(t => t._id === id ? updated : t));
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to update task.');
        setLoading(false);
      });
  };

  // Delete task
  const handleDelete = (id) => {
    setLoading(true);
    fetch(`http://localhost:5000/api/tasks/${id}`, { method: 'DELETE' })
      .then(res => res.json())
      .then(() => {
        setTasks(tasks.filter(t => t._id !== id));
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to delete task.');
        setLoading(false);
      });
  };

  // Start editing a task
  const startEdit = (task) => {
    setEditTaskId(task._id);
    setEditTask({ title: task.title, description: task.description });
  };

  // Save edited task
  const handleEditSave = (id) => {
    setLoading(true);
    fetch(`http://localhost:5000/api/tasks/${id}`, {
      method: 'PUT',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(editTask)
    })
      .then(res => res.json())
      .then(updated => {
        setTasks(tasks.map(t => t._id === id ? updated : t));
        setEditTaskId(null);
        setEditTask({ title: '', description: '' });
        setLoading(false);
      })
      .catch(() => {
        setError('Failed to edit task.');
        setLoading(false);
      });
  };

  // Cancel editing
  const handleEditCancel = () => {
    setEditTaskId(null);
    setEditTask({ title: '', description: '' });
  };

  // Filter tasks for selected date
  const tasksForDate = tasks.filter(t => t.date && t.date.slice(0, 10) === selectedDate);

  // Simple calendar: just a date picker
  return (
    <div className="App" style={{ maxWidth: 500, margin: '0 auto', padding: 20 }}>
      <h1>To-Do List Web App</h1>
      <div style={{ marginBottom: 20 }}>
        <label>
          ðŸ“… Select Date:
          <input
            type="date"
            value={selectedDate}
            onChange={e => setSelectedDate(e.target.value)}
            style={{ marginLeft: 10 }}
          />
        </label>
      </div>
      <form onSubmit={handleAddTask} style={{ marginBottom: 20 }}>
        <input
          type="text"
          placeholder="Task title"
          value={newTask.title}
          onChange={e => setNewTask({ ...newTask, title: e.target.value })}
          required
          style={{ marginRight: 10 }}
        />
        <input
          type="text"
          placeholder="Description (optional)"
          value={newTask.description}
          onChange={e => setNewTask({ ...newTask, description: e.target.value })}
          style={{ marginRight: 10 }}
        />
        <button type="submit" disabled={loading}>Add</button>
      </form>
      {error && <div style={{ color: 'red', marginBottom: 10 }}>{error}</div>}
      {loading && <div style={{ color: 'blue', marginBottom: 10 }}>Loading...</div>}
      <h2>Tasks for {selectedDate}</h2>
      <ul style={{ listStyle: 'none', padding: 0 }}>
        {tasksForDate.length === 0 && <li>No tasks for this date.</li>}
        {tasksForDate.map(task => (
          <li key={task._id} style={{ marginBottom: 10, textDecoration: task.done ? 'line-through' : 'none', display: 'flex', alignItems: 'center' }}>
            <input
              type="checkbox"
              checked={task.done}
              onChange={() => handleDone(task._id)}
              disabled={task.done || loading}
              style={{ marginRight: 8 }}
            />
            {editTaskId === task._id ? (
              <>
                <input
                  type="text"
                  value={editTask.title}
                  onChange={e => setEditTask({ ...editTask, title: e.target.value })}
                  style={{ marginRight: 8 }}
                />
                <input
                  type="text"
                  value={editTask.description}
                  onChange={e => setEditTask({ ...editTask, description: e.target.value })}
                  style={{ marginRight: 8 }}
                />
                <button onClick={() => handleEditSave(task._id)} disabled={loading}>Save</button>
                <button onClick={handleEditCancel} style={{ marginLeft: 4 }} disabled={loading}>Cancel</button>
              </>
            ) : (
              <>
                <strong>{task.title}</strong>
                {task.description && <span> - {task.description}</span>}
                <button onClick={() => startEdit(task)} style={{ marginLeft: 8 }} disabled={task.done || loading}>Edit</button>
                <button onClick={() => handleDelete(task._id)} style={{ marginLeft: 4 }} disabled={loading}>Delete</button>
              </>
            )}
          </li>
        ))}
      </ul>
    </div>
  );
}

export default App;
